(1786, 0, 36656, 50, 'S', 'CLS_2a', 'Could I please get your email address?', '', '<strong>RECORD EMAIL ADDRESS AND READ BACK TO THE PARTICIPANT TO VERIFY ACCURACY</strong>', 'N', 'Y', 14, 'en', 0, 0, '((TOKEN:EMAIL == "") and (36656X50X1785.NAOK == "YES_EMAIL"))'),
(1785, 0, 36656, 50, 'L', 'CLS_1', 'There are just a few more pieces of information that I would like to collect before we end the call. We will post regular updates about the study on the CLSA website. You can also receive annual updates by mail or email. Would you prefer either of these options? ', '', '', 'N', 'Y', 7, 'en', 0, 0, NULL),
(1796, 1790, 36656, 50, 'Q', 'ALT_TELEPHONE', 'INSCRIRE LE NO DE TÉLÉPHONE', NULL, NULL, 'N', '', 2, 'fr', 0, 0, NULL),
(1822, 0, 72154, 51, 'L', 'INT_0', 'CODE ONE OF THE FOLLOWING', '', '', 'N', 'Y', 0, 'en', 0, 0, NULL),
(1823, 0, 72154, 51, 'X', 'INT_1', '<p>\r\n	Hello, this message is for {TOKEN:FIRSTNAME} {TOKEN:LASTNAME}. My name is {TOKEN:ATTRIBUTE_1}, and Im calling from the Canadian Longitudinal Study on Aging, also known as the CLSA. We received your consent to contact you regarding participation in our study and I am calling to follow up.  I will try again another time. Or, you can contact us on our toll free line at 1-866-999-8303. Have a good day/ afternoon/ evening.</p>\r\n<p>\r\n	 </p>\r\n', '', '', 'N', 'N', 5, 'en', 0, 0, '((72154X51X1822.NAOK == "NO_ANSWER_IDENTIFY"))'),
(1824, 0, 72154, 51, 'X', 'INT_2', '<p>\r\n	INTERVIEWER: LEAVE THE FOLLOWING MESSAGE AFTER THE SECOND CALL ATTEMPT:</p>\r\n<p>\r\n	Hello, this is {TOKEN:ATTRIBUTE_1} from the Canadian Longitudinal Study on Aging, also known as the CLSA. I am trying to reach a person in your household that we understand is interested in participating in our study. I will try calling again another time. Or, you can contact us on our toll free line at 1-866-999-8303. Have a good day/ afternoon/ evening.</p>\r\n', '', '', 'N', 'N', 10, 'en', 0, 0, '((72154X51X1822.NAOK == "NO_ANSWER_NO_IDENTIFY"))'),
(1825, 0, 72154, 51, 'L', 'INT_3', 'Hello, may I please speak with {TOKEN:FIRSTNAME} {TOKEN:LASTNAME}?', '', '', 'N', 'Y', 13, 'en', 0, 0, NULL),
(1826, 0, 72154, 51, 'L', 'INT_4b', 'After we received your contact form we mailed you out a consent form for the study. Did you receive it?', '', '', 'N', 'Y', 15, 'en', 0, 0, '((TOKEN:ATTRIBUTE_3 == "ministry"))'),
(1871, 0, 67969, 54, 'T', 'HRF_1', '<p>\r\n	No problem, I appreciate your time. I do have one request before we end this call. It is very useful to have information on why people don''t want to participate, as it helps us make improvements to the study. Would you be willing to tell me why you do not want to participate?</p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	RECORD VERBATIM, PROBE AND CLARIFY AS REQUIRED</p>\r\n', '', '<p>\r\n	Thank you, we appreciate your feedback. Have a good day/ afternoon/ evening.</p>\r\n<p>\r\n	INTERVIEWER END CALL</p>\r\n', 'N', 'Y', 0, 'en', 0, 0, NULL),
(1827, 0, 72154, 51, 'L', 'INT_4c', '<p>\r\n	Hello, Mr./ Ms. {TOKEN:LASTNAME}. My name is {TOKEN:ATTRIBUTE_1}, and I am calling from the Canadian Longitudinal Study on Aging, also known as the CLSA. You were recently mailed an information package that describes the CLSA. Did you receive it?</p>\r\n', '', '', 'N', 'Y', 16, 'en', 0, 0, '((TOKEN:ATTRIBUTE_3 == "rdd"))'),
(1869, 0, 72154, 53, 'X', 'INT_12b', '<p>\r\n	I can see in the system we have received your written consent form in the mail.</p>\r\n<p>\r\n	Participation in the CLSA is completely voluntary and you may withdraw at any time. if you choose to withdraw from ths study at a later date, data already collected will remain part of the CLSA database, but we will not contact you for new information.</p>\r\n', '', '', 'N', 'N', 1, 'en', 0, 0, '((TOKEN:ATTRIBUTE_9 == "1"))'),
(1828, 0, 72154, 51, 'L', 'INT_4d', 'Before you discarded the information, did you have the chance to review its contents?', '', '', 'N', 'Y', 17, 'en', 0, 0, '((72154X51X1826.NAOK == "YES_CONSENT_DISCARDED")) or ((72154X51X1827.NAOK == "YES_PACKAGE_DISCARDED"))'),
(1829, 0, 72154, 51, 'L', 'INT_4e', 'Since you reviewed the information before you discarded it, would you like to proceed with the interview at this time and we can mail you a new package?', '', '', 'N', 'Y', 18, 'en', 0, 0, '((72154X51X1828.NAOK == "YES"))'),
(1830, 0, 72154, 51, 'L', 'INT_5', '<p>\r\n	OK, Id like to confirm the address that we have on file so that another package can be mailed to you. We have your mailing address as:</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_4}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_5}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_6}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_7}</p>\r\n<p>\r\n	Is this correct?</p>\r\n', '', '', 'N', 'Y', 19, 'en', 0, 0, '((72154X51X1826.NAOK == "DK_NS" or 72154X51X1826.NAOK == "NO" or 72154X51X1826.NAOK == "YES_CONSENT_DISCARDED") and (72154X51X1829.NAOK != "YES")) or ((72154X51X1827.NAOK == "DK_NS" or 72154X51X1827.NAOK == "NO" or 72154X51X1827.NAOK == "YES_PACKAGE_DISCARDED") and (72154X51X1829.NAOK != "YES")) or ((72154X51X1827.NAOK == "NO")) or ((72154X51X1826.NAOK == "NO")) or ((72154X51X1826.NAOK == "DK_NS")) or ((72154X51X1827.NAOK == "DK_NS")) or ((72154X51X1828.NAOK == "NO"))'),
(1831, 0, 72154, 51, 'X', 'INT_6', '<p>\r\n	Could I get your correct <u>MAILING</u> address, please?</p>\r\n<p>\r\n	INTERVIEWER: OPEN THE PARTICIPANT''S DETAILS IN SABRETOOTH AND UPDATE CURRENT ADDRESS</p>\r\n<p>\r\n	REPEAT BACK TO THE PARTICIPANT TO CONFIRM ACCURACY</p>\r\n', '', '', 'N', 'N', 20, 'en', 0, 0, '((72154X51X1830.NAOK == "NO"))'),
(1837, 0, 72154, 51, 'X', 'INT_7', '<p>\r\n	Great! A new package will be mailed to you in the next few days, and we will call you back in about a week to follow-up. Thank you for your time.</p>\r\n<p>\r\n	INTERVIEWER: END CALL & SCHEDULE GENERAL CALLBACK</p>\r\n', '', '', 'N', 'N', 21, 'en', 0, 0, '((72154X51X1829.NAOK == "NO_WAIT")) or ((72154X51X1828.NAOK == "NO")) or ((72154X51X1830.NAOK == "NO")) or ((72154X51X1830.NAOK == "YES"))'),
(1838, 0, 72154, 51, 'L', 'INT_8', '<p>\r\n	OK, Id like to confirm the address that we have on file so that another package can be mailed to you. We have your address as</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_4}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_5}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_6}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_7}</p>\r\n<p>\r\n	Is this correct?</p>\r\n', '', '', 'N', 'Y', 22, 'en', 0, 0, '((72154X51X1829.NAOK == "YES"))'),
(1839, 0, 72154, 51, 'X', 'INT_9', '<p>\r\n	Could I get your correct address, please?</p>\r\n<p>\r\n	INTERVIEWER: OPEN THE PARTICIPANT''S DETAILS IN SABRETOOTH AND UPDATE CURRENT ADDRESS</p>\r\n<p>\r\n	REPEAT BACK TO THE PARTICIPANT TO CONFIRM ACCURACY</p>\r\n', '', '', 'N', 'N', 23, 'en', 0, 0, '((72154X51X1838.NAOK == "NO"))'),
(1888, 0, 78958, 55, 'M', 'COG_WRDLST_TRM', 'I will begin the recording now. <span>Now, please tell me all the words you can remember in any order. Please begin.</span><br />\n', '', '<div>\n	<b>MULTIPLE RESPONSES ALLOWED, RECORD ALL WORDS THAT PARTICIPANT CORRECTLY RECALLS, NULL ALLOWED DK/RF NOT ALLOWED. </b><b>TIMER INSTRUCTIONS: DISPLAY COUNTDOWN TIMER FROM 90 SECONDS. TIMER CANNOT BE RESET OR PAUSED, BUT CAN BE STOPPED AT ANY TIME. A BEEP WILL SOUND WHEN THE TIMER COMPLETES.</b></div>\n<br />\n<br />\n', 'N', 'Y', 32, 'en', 0, 0, NULL),
(1845, 0, 72154, 51, 'L', 'INT_10', 'Great! A new package will be mailed to you in the next few days/weeks.  Would you like to begin the interview? ', '', '<strong>IF APPOINTMENT NEEDS TO BE SCHEDULED, BE SURE TO USE PROBES SUCH AS WHAT TIME OF DAY USUALLY WORKS BEST FOR YOU? OR WOULD YOU PREFER MORNING, AFTERNOON, OR EVENING?  SCHEDULE A DATE/ TIME FOR THE FIRST AVAILABLE OPENING.</strong>', 'N', 'Y', 24, 'en', 0, 0, '((72154X51X1829.NAOK == "YES"))'),
(1846, 0, 72154, 51, 'L', 'INT_11a', 'Excellent! Did you have a chance to review the information?', '', '', 'N', 'Y', 25, 'en', 0, 0, '((72154X51X1826.NAOK == "YES_HAS_CONSENT")) or ((72154X51X1827.NAOK == "YES_HAS_PACKAGE"))'),
(1847, 0, 72154, 51, 'L', 'INT_11b', 'Do you have any questions about the CLSA?', '', '', 'N', 'Y', 26, 'en', 0, 0, '((72154X51X1846.NAOK == "YES"))'),
(1848, 0, 72154, 51, 'L', 'INT_11c', '<p>\r\n	OK, let me tell you a little about the CLSA.  If you are an internet user, there is also information available to you 24 hours a day on our website at www.clsa-elcv.ca...</p>\r\n<p>\r\n	The CLSA is a Canada-wide study that will follow 50,000 Canadians aged 45 to 85 years of age. The information collected will be used by researchers to study the health of Canadians as they age. If you agree to participate, you will be interviewed over the telephone once every 3 years, and contacted briefly by phone in between. The types of questions asked will relate to your health, lifestyle, visits to healthcare providers, employment and retirement activities, and social support networks. Would you like to take part in this study?</p>\r\n', '', '', 'N', 'Y', 27, 'en', 0, 0, '((72154X51X1846.NAOK == "NO"))'),
(1849, 0, 72154, 51, 'L', 'INT_11d', '<p>\r\n	OK, let me try to answer your questions.</p>\r\n<p>\r\n	INTERVIEWER: ALLOW PARTICIPANT TO ASK QUESTIONS AND REFER TO THE INFORMATION PACKAGE CONTENTS TO ANSWER APPROPRIATELY.</p>\r\n', '', '', 'N', 'Y', 28, 'en', 0, 0, '((72154X51X1847.NAOK == "YES"))'),
(1850, 0, 72154, 51, 'L', 'INT_11e', 'Now that I have answered all your questions to your satisfaction, are you interested in becoming a participant in the CLSA?', '', '', 'N', 'Y', 29, 'en', 0, 0, '((72154X51X1847.NAOK == "YES"))'),
(1851, 0, 72154, 52, 'X', 'INCL_1', 'Before we go into the initial interview we need to confirm that you meet the Inclusion Criteria of the CLSA, and also ensure that you DO NOT meet any of the Exclusion Criteria.', '', '', 'N', 'N', 0, 'en', 0, 0, NULL),
(1852, 0, 72154, 52, 'L', 'INCL_2a', 'Are you between the ages of 45  85 years old?', '', '', 'N', 'Y', 1, 'en', 0, 0, NULL),
(1853, 0, 72154, 52, 'L', 'INCL_2b', 'Will you be  turning 45 in the next 6 months or have you turned 86 in the last 6 months? ', '', '', 'N', 'Y', 2, 'en', 0, 0, '((72154X52X1852.NAOK == "NO"))'),
(1854, 0, 72154, 52, 'L', 'INCL_2c', 'Are you a Canadian Citizen, landed immigrant or Permanent resident of Canada?', '', '', 'N', 'Y', 3, 'en', 0, 0, '((72154X52X1853.NAOK == "YES")) or ((72154X52X1852.NAOK == "YES"))'),
(1855, 0, 72154, 52, 'L', 'INCL_2d', 'Do you reside on a Federal First Nations Reserve, a First Nation settlement or Crown land?', '', '', 'N', 'Y', 4, 'en', 0, 0, '((72154X52X1854.NAOK == "YES"))'),
(1856, 0, 72154, 52, 'L', 'INCL_2e', 'Are you currently a FULL TIME member of the Canadian Armed Forces?', '', '', 'N', 'Y', 5, 'en', 0, 0, '((72154X52X1855.NAOK == "NO"))'),
(1857, 0, 72154, 52, 'L', 'INCL_2f', '<p>\r\n	Are you currently residing in a Long-term care institution?</p>\r\n', '', 'DEFINITION OF INSTITUTION:<br />\r\n<p>\r\n	Institutional collective dwellings are general hospitals and hospitals with emergency, other hospitals and related institutions, nursing homes and facilities for persons with a disability</p>\r\n', 'N', 'Y', 6, 'en', 0, 0, '((72154X52X1856.NAOK == "NO"))'),
(1858, 0, 72154, 52, 'X', 'INCL_3', 'I am sorry but you meet one of the exclusion criteria of the Canadian Longitudinal Study on Aging.  Unfortunately we will not be able to continue with an interview.  I would like to thank you for taking the time to speak with us. Have a good day/ afternoon/ evening.', '', 'END CALL', 'N', 'N', 7, 'en', 0, 0, '((72154X52X1852.NAOK == "NO") and (72154X52X1853.NAOK == "NO")) or ((72154X52X1854.NAOK == "NO")) or ((72154X52X1855.NAOK == "YES")) or ((72154X52X1856.NAOK == "YES")) or ((72154X52X1857.NAOK == "YES"))'),
(1859, 0, 72154, 53, 'L', 'INT_12a', '<p>\r\n	Participation in the CLSA is completely voluntary and you may withdraw at any time. If you choose to withdraw from the study at a later date, data already collected will remain part of the CLSA database, but we will not contact you for new information. Would you like to participate in the study?</p>\r\n', '', '', 'N', 'Y', 0, 'en', 0, 0, '((TOKEN:ATTRIBUTE_9 != "1"))'),
(1860, 0, 72154, 53, 'L', 'INT_13a', 'In addition to participating in the study, we would also like to ask you to provide your Provincial Health Card Number so that we may link information collected from you with information contained in other databases held by public institutions. Do you agree to provide us with your Provincial Health Card Number?', '', '', 'N', 'Y', 2, 'en', 0, 0, '((TOKEN:ATTRIBUTE_8 == "-1"))'),
(1861, 0, 72154, 53, 'L', 'INT_14a', '<p>\r\n	Great! Please check off the yes boxes on the light green consent form that was included in the package. Then print and sign your name and date at the bottom and mail it back to us in the pre-paid self-addressed envelope provided.  The second copy of this form is for you to keep for your records.<strong> </strong></p>\r\n<p>\r\n	INTERVIEWER  ASK THE PARTICIPANT TO CONFIRM THAT THEY STILL HAVE BOTH GREEN AND WHITE CONSENT FORMS IN THEIR POSSESSION</p>\r\n', '', '', 'N', 'Y', 4, 'en', 0, 0, '((72154X51X1826.NAOK == "YES_HAS_CONSENT") and (72154X53X1859.NAOK == "YES") and (72154X53X1860.NAOK == "YES")) or ((72154X51X1827.NAOK == "YES_HAS_PACKAGE") and (72154X53X1859.NAOK == "YES") and (72154X53X1860.NAOK == "YES"))'),
(1862, 0, 72154, 53, 'L', 'INT_14b', '<p>\r\n	Thats okay, you are still part of the study. Please make sure you have checked yes to Option #1 and no to Option #2 on the light green consent form that was included in the Information Package. Then print and sign your name and date at the bottom and mail it back to us in the pre-paid self-addressed envelope provided. The second copy of this form is for you to keep for your records.  </p>\r\n<p>\r\n	INTERVIEWER  ASK THE PARTICIPANT TO CONFIRM THAT THEY STILL HAVE BOTH GREEN AND WHITE CONSENT FORMS IN THEIR POSSESSION</p>\r\n', '', '', 'N', 'Y', 5, 'en', 0, 0, '((72154X51X1826.NAOK == "YES_HAS_CONSENT") and (72154X53X1859.NAOK == "YES") and (72154X53X1860.NAOK == "NO")) or ((72154X51X1827.NAOK == "YES_HAS_PACKAGE") and (72154X53X1859.NAOK == "YES") and (72154X53X1860.NAOK == "NO"))'),
(1870, 0, 72154, 53, 'X', 'INT_13b', '<p>\r\n	Sur votre formulaire de consentement écrit que vous avons reçu, vous avez aussi donné votre consentement pour donner votre numéro dassurance-maladie provincial pour que nous puissions établir un lien entre linformation que vous nous fournirez et celle contenue dans dautres bases de données tenues par des institutions publiques. Nous le prendrons en note plus tard pendant lentrevue.</p>\r\n<p>\r\n	 </p>\r\n', '', '', 'N', 'N', 3, 'fr', 0, 0, '((TOKEN:ATTRIBUTE_8 == "1") and (TOKEN:ATTRIBUTE_9 == "1"))'),
(1866, 0, 72154, 53, 'L', 'INT_15', '<p>\r\n	Do you have about an hour to participate in the initial interview now?</p>\r\n<p>\r\n	<strong>IF APPOINTMENT NEEDS TO BE SCHEDULED, BE SURE TO USE PROBES SUCH AS WHAT TIME OF DAY USUALLY WORKS BEST FOR YOU? OR WOULD YOU PREFER MORNING, AFTERNOON, OR EVENING?  SCHEDULE A DATE/ TIME FOR THE FIRST AVAILABLE OPENING.</strong></p>\r\n', '', '', 'N', 'Y', 6, 'en', 0, 0, NULL),
(1867, 0, 72154, 53, 'L', 'INT_16', 'Great, let''s get started!', '', '', 'N', 'Y', 7, 'en', 0, 0, NULL),
(1787, 0, 36656, 50, 'L', 'CLS_2b', 'Selon nos dossiers, votre adresse courriel est {TOKEN:EMAIL}. Est-ce exact?\r\n<p>\r\n	 </p>\r\n', '', '', 'N', 'Y', 20, 'fr', 0, 0, '((TOKEN:EMAIL != "") and (36656X50X1785.NAOK == "YES_EMAIL"))'),
(1788, 0, 36656, 50, 'S', 'CLS_2c', 'Pourrais-je avoir votre bonne adresse courriel? ', '', '<strong>INSCRIRE LADRESSE COURRIEL ET LA RÉPÉTER AU PARTICIPANT POUR EN CONFIRMER LEXACTITUDE</strong>', 'N', 'Y', 22, 'fr', 0, 0, '((TOKEN:EMAIL != "") and (36656X50X1787.NAOK == "NO"))'),
(1789, 0, 36656, 50, 'L', 'CLS_3', 'Si, éventuellement, nous étions incapables de vous vous rejoindre directement, jaimerais que vous me donniez les coordonnées de deux personnes qui pourraient nous mettre en contact avec vous. Pensez à des personnes qui savent toujours où vous êtes, que vous déménagiez ou que vous vous absentiez pour un certain temps. Pourriez-vous nous donner les coordonnées dune personne qui pourrait agir comme ce contact?', '', '', 'N', 'Y', 24, 'fr', 0, 0, NULL),
(1790, 0, 36656, 50, 'Q', 'CLS_4', 'Daccord, pourrais-je avoir le nom et le numéro de téléphone dune personne qui pourrait être votre première personne contact et pourrais-je savoir quel est son lien avec vous?', '', '', 'N', 'Y', 26, 'fr', 0, 0, '((36656X50X1789.NAOK == "YES"))'),
(1798, 0, 36656, 50, 'Q', 'CLS_5', 'Au cas où la première personne ne pourrait être rejointe, pouvez-vous me donner le nom et le numéro de téléphone dune deuxième personne et pourrais-je savoir quel est son lien avec vous?', '', '<p>\r\n	LA QUESTION N''EST PAS OBLIGATOIRE, PEUT RESTER VIDE EN CAS DE REFUS.</p>\r\n', 'N', 'N', 28, 'fr', 0, 0, '((36656X50X1789.NAOK == "YES"))'),
(1791, 0, 36656, 50, 'L', 'CLS_6', 'Certains de nos participants ont une résidence secondaire dans laquelle ils vivent lété ou lhiver. Nous aimerions connaître les coordonnées de ces résidences secondaires pour pouvoir correspondre avec vous ou vous joindre pour des entrevues de suivi en tout temps. Possédez-vous une résidence secondaire pour laquelle vous voudriez nous donner ladresse?', '', '', 'N', 'Y', 30, 'fr', 0, 0, NULL),
(1792, 0, 36656, 50, 'X', 'CLS_7a', '<p>\r\n	Très bien, allez-y en commençant par ladresse et les mois pendant lesquels cette adresse est active.</p>\r\n<p>\r\n	<br />\r\n	INTERVIEWEUR : OUVRIR SABRETOOTH ET AJOUTER (BOUTON [+]) UNE ADRESSE ET LES MOIS PENDANT LESQUELS CETTE ADRESSE EST ACTIVE.</p>\r\n', '', 'INSCRIRE LADRESSE ET LA RÉPÉTER AU PARTICIPANT POUR EN CONFIRMER LEXACTITUDE', 'N', 'N', 32, 'fr', 0, 0, '((36656X50X1791.NAOK == "YES"))'),
(1793, 0, 36656, 50, 'X', 'CLS_7b', '<p>\r\n	Pouvez-vous me donner le numéro de téléphone de votre résidence secondaire?</p>\r\nINTERVIEWEUR : OUVRIR SABRETOOTH ET AJOUTER (BOUTON [+]) UN NUMÉRO DE TÉLÉPHONE ET ASSOCIER CE NUMÉRO AVEC LADRESSE QUI VIENT DÊTRE AJOUTÉE', '', '<p>\r\n	INSCRIRE LE NUMÉRO DE TÉLÉPHONE ET LE RÉPÉTER AU PARTICIPANT POUR EN CONFIRMER LEXACTITUDE</p>\r\n', 'N', 'N', 34, 'fr', 0, 0, '((36656X50X1791.NAOK == "YES"))'),
(1794, 0, 36656, 50, 'X', 'CLS_8', '<p>\r\n	Mr / Mme  {TOKEN:LASTNAME}, merci du temps que vous mavez accordé aujourdhui. Un de nos chercheurs communiquera avec vous dans environ 12 à 18 mois pour planifier une autre entrevue.</p>\r\n<script type="text/javascript" charset="utf-8">\r\n  var attribute_1 = ''{TOKEN:ATTRIBUTE_1}'';\r\n  if(attribute_1 == 0)\r\n  {\r\n    document.write("<p><br />Et noubliez pas de nous envoyer par la poste votre formulaire de consentement signé afin que nous puissions continuer les entrevues avec vous.</p>");\r\n  }\r\n</script>\r\n<p>\r\n	Merci encore, et bienvenue dans lÉLCV. Bonne journée / bon après-midi / bonne soirée.</p>\r\n', '', '', 'N', 'N', 36, 'fr', 0, 0, NULL),
(1822, 0, 72154, 51, 'L', 'INT_0', 'INSCRIRE LUN DES CODES SUIVANTS<br />\r\n', '', '', 'N', 'Y', 0, 'fr', 0, 0, NULL),
(1851, 0, 72154, 52, 'X', 'INCL_1', 'Avant de commencer la première entrevue, nous devons nous assurer que vous répondez aux critères dinclusion de lÉLCV et que vous ne répondez à AUCUN des critères dexclusion.', '', '', 'N', 'N', 0, 'fr', 0, 0, NULL),
(1859, 0, 72154, 53, 'L', 'INT_12a', '<p>\r\n	La participation à lÉLCV est entièrement volontaire et vous pouvez vous retirer en tout temps. Plus tard, si vous décidez de ne plus participer à létude, les données déjà recueillies resteront dans la base de données de lÉLCV, mais nous ne communiquerons pas avec vous pour obtenir de nouveaux renseignements. Est-ce que vous aimeriez participer à létude?</p>\r\n', '', '', 'N', 'Y', 0, 'fr', 0, 0, '((TOKEN:ATTRIBUTE_9 != "1"))'),
(1852, 0, 72154, 52, 'L', 'INCL_2a', '<p>Êtes-vous âgé(e) entre 45 et 85 ans?</p>\r\n', '', '', 'N', 'Y', 1, 'fr', 0, 0, NULL),
(1860, 0, 72154, 53, 'L', 'INT_13a', '<p>\r\n	En plus de votre participation à létude, nous aimerions aussi vous demander de nous fournir votre numéro de carte santé (carte dassurance maladie) provincial afin que nous puissions établir un lien entre linformation que vous nous fournirez et celle contenue dans dautres bases de données tenues par des institutions publiques. Est-ce que vous acceptez de nous donner votre numéro dassurance maladie?</p>\r\n', '', '', 'N', 'Y', 2, 'fr', 0, 0, '((TOKEN:ATTRIBUTE_8 == "-1"))'),
(1853, 0, 72154, 52, 'L', 'INCL_2b', 'Aurez-vous 45 ans dans les 6 prochains mois ou avez-vous eu 86 ans dans les 6 derniers mois?', '', '', 'N', 'Y', 2, 'fr', 0, 0, '((72154X52X1852.NAOK == "NO"))'),
(1861, 0, 72154, 53, 'L', 'INT_14a', 'Très bien! Veuillez cocher la caseoui sur le formulaire de consentement vert pâle faisant partie de la trousse dinformation. Veuillez ensuite inscrire votre nom en caractères d''imprimerie et signer au bas du formulaire en indiquant la date, et nous le retourner dans lenveloppe-réponse préaffranchie prévue à cet effet. La seconde copie du formulaire est à conserver dans vos dossiers. <strong>INTERVIEWEUR  DEMANDEZ AU PARTICIPANT DE CONFIRMER QUILS ONT BIEN EN LEUR POSSESSION LA COPIE VERTE ET LA COPIE BLANCHE DU FORMULAIRE DE CONSENTEMENT.</strong>', '', '', 'N', 'Y', 4, 'fr', 0, 0, '((72154X51X1826.NAOK == "YES_HAS_CONSENT") and (72154X53X1859.NAOK == "YES") and (72154X53X1860.NAOK == "YES")) or ((72154X51X1827.NAOK == "YES_HAS_PACKAGE") and (72154X53X1859.NAOK == "YES") and (72154X53X1860.NAOK == "YES"))'),
(1854, 0, 72154, 52, 'L', 'INCL_2c', 'Êtes-vous un(e) citoyen(ne) canadien(ne), immigrant(e) reçu(e) ou résident(e) permanent(e) du Canada?', '', '', 'N', 'Y', 3, 'fr', 0, 0, '((72154X52X1853.NAOK == "YES")) or ((72154X52X1852.NAOK == "YES"))'),
(1862, 0, 72154, 53, 'L', 'INT_14b', 'Ça va, vous faites toujours partie de létude. Veuillez vous assurer que vous avez cochéoui à loption 1 etnon à loption 2 sur le formulaire de consentement vert pâle faisant partie de la trousse dinformation.  Ensuite, veuillez inscrire votre nom en caractères d''imprimerie et signer au bas du formulaire, en indiquant la date, et nous le retourner dans lenveloppe-réponse préaffranchie prévue à cet effet. <strong>INTERVIEWEUR  DEMANDEZ AU PARTICIPANT DE CONFIRMER QUILS ONT BIEN EN LEUR POSSESSION LA COPIE VERTE ET LA COPIE BLANCHE DU FORMULAIRE DE CONSENTEMENT.</strong>', '', '', 'N', 'Y', 5, 'fr', 0, 0, '((72154X51X1826.NAOK == "YES_HAS_CONSENT") and (72154X53X1859.NAOK == "YES") and (72154X53X1860.NAOK == "NO")) or ((72154X51X1827.NAOK == "YES_HAS_PACKAGE") and (72154X53X1859.NAOK == "YES") and (72154X53X1860.NAOK == "NO"))'),
(1855, 0, 72154, 52, 'L', 'INCL_2d', 'Habitez-vous dans une réserve des Premières nations relevant du gouvernement fédéral, ou dans un autre établissement des Premières nations dans les provinces ou sur les terres de la Couronne?', '', '', 'N', 'Y', 4, 'fr', 0, 0, '((72154X52X1854.NAOK == "YES"))'),
(1823, 0, 72154, 51, 'X', 'INT_1', 'Bonjour / bonsoir, ce message sadresse à M. / Mme {TOKEN:FIRSTNAME}<span style="font-weight: bold;"> {TOKEN:LASTNAME}</span>.  Mon nom est {TOKEN:ATTRIBUTE_1}<strong>. </strong>Je vous appelle au sujet de lÉtude longitudinale canadienne sur le vieillissement ou ÉLCV. Nous avons reçu le formulaire dans lequel vous consentiez à ce quon vous contacte. Jappelle aujourdhui, pour en faire le suivi. Jessaierai de vous rejoindre à un autre moment ou, si vous préférez, vous pouvez communiquer avec nous à notre numéro sans frais, le 1 866 999-8303. Bonne journée / bon après-midi / bonne soirée.', '', '', 'N', 'N', 5, 'fr', 0, 0, '((72154X51X1822.NAOK == "NO_ANSWER_IDENTIFY"))'),
(1856, 0, 72154, 52, 'L', 'INCL_2e', 'Êtes-vous membre À TEMPS PLEIN des Forces armées canadiennes?', '', '', 'N', 'Y', 5, 'fr', 0, 0, '((72154X52X1855.NAOK == "NO"))'),
(1869, 0, 72154, 53, 'X', 'INT_12b', '<p>\r\n	Je vois que nous avons reçu votre consentement écrit par la poste.<br />\r\n	<br />\r\n	    La participation à lÉLCV est entièrement volontaire et vous pouvez vous retirer en tout temps. Plus tard, si vous décidez de ne plus participer à létude, les données déjà recueillies resteront dans la base de données de lÉLCV, mais nous ne communiquerons pas avec vous pour obtenir de nouveaux renseignements.</p>\r\n<p>\r\n	 </p>\r\n', '', '', 'N', 'N', 1, 'fr', 0, 0, '((TOKEN:ATTRIBUTE_9 == "1"))'),
(1870, 0, 72154, 53, 'X', 'INT_13b', 'On the written consent we received in the mail you also consented to provide your Provincial Health Insurance number so that we may link information collected from you with the information contained in other databases held by public institutions. We will collect this from you later on in the interview.', '', '', 'N', 'N', 3, 'en', 0, 0, '((TOKEN:ATTRIBUTE_8 == "1") and (TOKEN:ATTRIBUTE_9 == "1"))'),
(1857, 0, 72154, 52, 'L', 'INCL_2f', '<p>\r\n	Habitez-vous présentement dans un établissement de soins de longue durée?</p>\r\n', '', '<strong>DÉFINITION DÉTABLISSEMENT :<br />\r\nLieux institutionnels communautaires : hôpitaux, hôpitaux avec salle durgence, autres type dhôpitaux et établissements connexes, établissements de soins de longue durée et centres dhébergement pour personnes handicapées.</strong>', 'N', 'Y', 6, 'fr', 0, 0, '((72154X52X1856.NAOK == "NO"))'),
(1858, 0, 72154, 52, 'X', 'INCL_3', 'Je suis désolé, vous répondez à un des critères dexclusion de lÉtude longitudinale canadienne sur le vieillissement. Malheureusement, nous ne pourrons pas faire lentrevue. Merci beaucoup davoir pris le temps de nous parler. Bonne journée / bon après-midi / bonne soirée.<br />\r\n ', '', 'END CALL', 'N', 'N', 7, 'fr', 0, 0, '((72154X52X1852.NAOK == "NO") and (72154X52X1853.NAOK == "NO")) or ((72154X52X1854.NAOK == "NO")) or ((72154X52X1855.NAOK == "YES")) or ((72154X52X1856.NAOK == "YES")) or ((72154X52X1857.NAOK == "YES"))'),
(1866, 0, 72154, 53, 'L', 'INT_15', '<p>\r\n	Est-ce que vous disposez denviron une heure pour participer à la première entrevue maintenant?</p>\r\n<p>\r\n	<strong>SIL EST NÉCESSAIRE DE PRENDRE RENDEZ-VOUS, NE PAS HÉSITER À DEMANDER DES PRÉCISIONS TELLES QUEHABITUELLEMENT, QUEL EST LE MEILLEUR MOMENT DE LA JOURNÉE POUR VOUS? ouPRÉFÉREZ-VOUS LE MATIN, LAPRÈS-MIDI OU LE SOIR?  FIXER LA DATE ET LHEURE DU RENDEZ-VOUS  SELON LE PREMIER MOMENT DISPONIBLE.</strong></p>\r\n', '', '', 'N', 'Y', 6, 'fr', 0, 0, NULL),
(1867, 0, 72154, 53, 'L', 'INT_16', 'Très bien, allons-y! ', '', '', 'N', 'Y', 7, 'fr', 0, 0, NULL),
(1824, 0, 72154, 51, 'X', 'INT_2', '<p>\r\n	<strong>LAISSER LE MESSAGE SUIVANT APRÈS LA DEUXIÈME TENTATIVE DAPPEL :  </strong></p>\r\n<p>\r\n	Bonjour / bonsoir, je mappelle {TOKEN:ATTRIBUTE_1} de lÉtude longitudinale canadienne sur le vieillissement, ou lÉLCV. Je tente actuellement de joindre une personne de votre ménage qui, nous croyons comprendre, souhaite participer à notre étude. Jessaierai de vous rejoindre à un autre moment. Aussi, vous pouvez communiquer avec nous à notre numéro sans frais, le 1 866 999-8303. Bonne journée / bon après-midi / bonne soirée.</p>\r\n', '', '', 'N', 'N', 10, 'fr', 0, 0, '((72154X51X1822.NAOK == "NO_ANSWER_NO_IDENTIFY"))'),
(1825, 0, 72154, 51, 'L', 'INT_3', 'Bonjour / bonsoir, puis-je parler à {TOKEN:FIRSTNAME} {TOKEN:LASTNAME}?', '', '', 'N', 'Y', 13, 'fr', 0, 0, NULL),
(1826, 0, 72154, 51, 'L', 'INT_4b', 'Quand nous avons reçu votre formulaire de coordonnées, nous vous avons envoyé un formulaire de consentement pour létude. Lavez-vous reçu?', '', '', 'N', 'Y', 15, 'fr', 0, 0, '((TOKEN:ATTRIBUTE_3 == "ministry"))'),
(1827, 0, 72154, 51, 'L', 'INT_4c', '<p>\r\n	Bonjour / bonsoir M. / Mme {TOKEN:LASTNAME}.  Mon nom est {TOKEN:ATTRIBUTE_1}. Je vous appelle au sujet de lÉtude longitudinale canadienne sur le vieillissement ou lÉLCV. Nous vous avons récemment fait parvenir par la poste une trousse dinformation à propos de notre étude. Est-ce que vous lavez reçue?</p>\r\n', '', '', 'N', 'Y', 16, 'fr', 0, 0, '((TOKEN:ATTRIBUTE_3 == "rdd"))'),
(1828, 0, 72154, 51, 'L', 'INT_4d', 'Avant de jeter la trousse, avez-vous eu loccasion den examiner le contenu?', '', '', 'N', 'Y', 17, 'fr', 0, 0, '((72154X51X1826.NAOK == "YES_CONSENT_DISCARDED")) or ((72154X51X1827.NAOK == "YES_PACKAGE_DISCARDED"))'),
(1829, 0, 72154, 51, 'L', 'INT_4e', 'Comme vous avez examiné son contenu avant de la jeter, aimeriez-vous faire lentrevue maintenant et on pourra vous envoyer une nouvelle trousse?', '', '', 'N', 'Y', 18, 'fr', 0, 0, '((72154X51X1828.NAOK == "YES"))'),
(1830, 0, 72154, 51, 'L', 'INT_5', '<p>\r\n	Daccord, jaimerais confirmer votre adresse afin de vous faire parvenir une autre trousse. Ladresse que nous avons est :</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_4}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_5}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_6}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_7}</p>\r\n<p>\r\n	Est-ce exact?</p>\r\n', '', '', 'N', 'Y', 19, 'fr', 0, 0, '((72154X51X1826.NAOK == "DK_NS" or 72154X51X1826.NAOK == "NO" or 72154X51X1826.NAOK == "YES_CONSENT_DISCARDED") and (72154X51X1829.NAOK != "YES")) or ((72154X51X1827.NAOK == "DK_NS" or 72154X51X1827.NAOK == "NO" or 72154X51X1827.NAOK == "YES_PACKAGE_DISCARDED") and (72154X51X1829.NAOK != "YES")) or ((72154X51X1827.NAOK == "NO")) or ((72154X51X1826.NAOK == "NO")) or ((72154X51X1826.NAOK == "DK_NS")) or ((72154X51X1827.NAOK == "DK_NS")) or ((72154X51X1828.NAOK == "NO"))'),
(1831, 0, 72154, 51, 'X', 'INT_6', '<p>\r\n	Pourrais-je avoir votre adresse <u>POSTALE</u> exacte, sil vous plaît?</p>\r\nNOTE À LINTERVIEWEUR : OUVRIR LES INFORMATIONS DU PARTICIPANT DANS SABRETOOTH ET METTRE À JOUR LADRESSE ACTUELLE<br />\r\n<br />\r\nLA RÉPÉTER AU PARTICIPANT POUR EN CONFIRMER LEXACTITUDE', '', '', 'N', 'N', 20, 'fr', 0, 0, '((72154X51X1830.NAOK == "NO"))'),
(1837, 0, 72154, 51, 'X', 'INT_7', '<p>\r\n	Très bien! Une nouvelle trousse vous sera envoyée dans les prochains jours et nous vous rappellerons dans une semaine pour faire le suivi. Merci beaucoup d''avoir pris le temps de nous parler.</p>\r\n<p>\r\n	INTERVIEWEUR : TERMINER LAPPEL ET PLANIFIER UN RAPPEL GÉNÉRAL</p>\r\n', '', '', 'N', 'N', 21, 'fr', 0, 0, '((72154X51X1829.NAOK == "NO_WAIT")) or ((72154X51X1828.NAOK == "NO")) or ((72154X51X1830.NAOK == "NO")) or ((72154X51X1830.NAOK == "YES"))'),
(1838, 0, 72154, 51, 'L', 'INT_8', '<p>\r\n	Daccord, jaimerais confirmer votre adresse afin de vous faire parvenir une autre trousse. Ladresse que nous avons est</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_4}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_5}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_6}</p>\r\n<p>\r\n	{TOKEN:ATTRIBUTE_7}</p>\r\n<p>\r\n	Est-ce exact?</p>\r\n', '', '', 'N', 'Y', 22, 'fr', 0, 0, '((72154X51X1829.NAOK == "YES"))'),
(1839, 0, 72154, 51, 'X', 'INT_9', '<p>\r\n	Pourrais-je avoir votre adresse exacte, sil vous plaît?</p>\r\n<p>\r\n	 </p>\r\n<p>\r\n	NOTE À LINTERVIEWEUR : OUVRIR LES INFORMATIONS DU PARTICIPANT DANS SABRETOOTH ET METTRE À JOUR LADRESSE ACTUELLE<br />\r\n	<br />\r\n	LA RÉPÉTER AU PARTICIPANT POUR EN CONFIRMER LEXACTITUDE</p>\r\n', '', '', 'N', 'N', 23, 'fr', 0, 0, '((72154X51X1838.NAOK == "NO"))'),
(1845, 0, 72154, 51, 'L', 'INT_10', 'Très bien! Une nouvelle trousse dinformation vous sera envoyée dans les prochains jours ou dans les prochaines semaines. Êtes-vous prêt(e) à commencer lentrevue ? ', '', '<p>SI LE RAPPEL DOIT ÊTRE PLANIFIÉ, VOUS ASSURER DE DEMANDER DES PRÉCISIONS TELLES QUEQUELLE PÉRIODE DE LA JOURNÉE VOUS CONVIENDRAIT LE MIEUX? OUPRÉFÉRÉZ-VOUS QUON VOUS APPELLE LE MATIN, LAPRÈS-MIDI OU EN SOIRÉE? - PLANIFIER UNE DATE ET UNE HEURE POUR LA PROCHAINE PLAGE HORAIRE LIBRE.</p>\r\n', 'N', 'Y', 24, 'fr', 0, 0, '((72154X51X1829.NAOK == "YES"))'),
(1846, 0, 72154, 51, 'L', 'INT_11a', 'Très bien! Avez-vous eu loccasion den examiner le contenu?', '', '', 'N', 'Y', 25, 'fr', 0, 0, '((72154X51X1826.NAOK == "YES_HAS_CONSENT")) or ((72154X51X1827.NAOK == "YES_HAS_PACKAGE"))'),
(1847, 0, 72154, 51, 'L', 'INT_11b', 'Avez-vous des questions concernant lÉLCV?', '', '', 'N', 'Y', 26, 'fr', 0, 0, '((72154X51X1846.NAOK == "YES"))'),
(1848, 0, 72154, 51, 'L', 'INT_11c', '<p>\r\n	Daccord, maintenant permettez-moi de vous parler un peu de lÉLCV. Si vous utilisez Internet, vous pouvez aussi obtenir de linformation 24 heures sur 24 en visitant notre site Web au www.clsa-elcv.ca...</p>\r\n<p>LÉLCV est une étude pancanadienne qui permettra de suivre 50 000 Canadiens et Canadiennes âgés de 45 à 85 ans. Linformation recueillie sera utilisée par les chercheurs pour étudier la santé des Canadiens et des Canadiennes au fur et à mesure quils vieillissent. Si vous acceptez de participer, vous accordez une entrevue téléphonique une fois aux trois ans et une autre brève entrevue téléphonique à mi-chemin entre les trois ans. Les questions posées porteront sur votre santé, votre mode de vie, vos visites chez des professionnels de la santé, votre emploi, vos activités lors de la retraite et votre réseau de soutien social. Aimeriez-vous participer à létude?</p>\r\n', '', '', 'N', 'Y', 27, 'fr', 0, 0, '((72154X51X1846.NAOK == "NO"))'),
(1849, 0, 72154, 51, 'L', 'INT_11d', '<p>\r\n	Daccord, je vais tenter de répondre à vos questions.</p>\r\n<p>\r\n	<span style="display: none;"> </span>LAISSER LE PARTICIPANT POSER DES QUESTIONS ET CONSULTER LA TROUSSE DINFORMATION POUR Y RÉPONDRE DE FAÇON APPROPRIÉE.<span style="display: none;"> </span></p>\r\n', '', '', 'N', 'Y', 28, 'fr', 0, 0, '((72154X51X1847.NAOK == "YES"))'),
(1850, 0, 72154, 51, 'L', 'INT_11e', '<p>Maintenant que jai répondu à vos questions à votre pleine satisfaction, êtes-vous intéressé(e) à participer à lÉLCV?</p>\r\n', '', '', 'N', 'Y', 29, 'fr', 0, 0, '((72154X51X1847.NAOK == "YES"))'),
(1868, 0, 72154, 51, 'L', 'INT_4a', 'Hello, Mr./ Ms. {TOKEN:LASTNAME}. My name is {TOKEN:ATTRIBUTE_1}, and I am calling from the Canadian Longitudinal Study on Aging, also known as the CLSA. We received your contact form giving us permission to contact you in regards to participating in the study. Can you tell me, were you the ORIGINAL person in your household who received the invitation package from your Ministry of Health in {TOKEN:ATTRIBUTE_6}?', '', '', 'N', 'Y', 14, 'en', 0, 0, '((TOKEN:ATTRIBUTE_3 == "ministry"))'),
(1868, 0, 72154, 51, 'L', 'INT_4a', 'Bonjour / bonsoir M. / Mme {TOKEN:LASTNAME}<strong>. </strong> Mon nom est {TOKEN:ATTRIBUTE_1}<strong>. </strong>Je vous appelle au sujet de lÉtude longitudinale canadienne sur le vieillissement ou lÉLCV. Nous avons reçu votre formulaire de coordonnées nous donnant la permission de communiquer avec vous à propos de votre participation à létude. Dites-moi, êtes-vous la personne à qui le ministère de la santé du {TOKEN:ATTRIBUTE_6} a initialement envoyé linvitation ??', '', '', 'N', 'Y', 14, 'fr', 0, 0, '((TOKEN:ATTRIBUTE_3 == "ministry"))'),
(1888, 0, 78958, 55, 'M', 'COG_WRDLST_TRM', 'Je vais commencer lenregistrement maintenant. Dites-moi maintenant tous les mots dont vous vous souvenez, dans nimporte quel ordre. Veuillez commencer. ', '', '<div>\n	<p>\n		<strong>RÉPONSES MULTIPLES PERMISES, INSCRIRE TOUS LES MOTS DONT LE PARTICIPANTSE SOUVIENT CORRECTEMENT, NSP / RF NON PERMIS. </strong><strong>INSTRUCTIONS POUR LE CHRONOMÈTRE : AFFICHER LE COMPTE À REBOURS À 90 SECONDES. ON NE PEUT PAS REDÉMARRER LE CHRONOMÈTRE OU LE METTRE SUR PAUSE, MAIS IL PEUT ÊTRE ARRÊTÉ. LE BIP SE FERA ENTENDRE LORSQUE LE CHRONOMÈTRE SERA ARRÊTÉ. NUL PERMIS, NSP / RF NON PERMIS</strong></p>\n</div>\n<br />\n<br />\n', 'N', 'Y', 32, 'fr', 0, 0, NULL),
(1894, 0, 78958, 55, 'N', 'COG_ANMLLIST_TRM', '<script type="text/javascript" charset="utf-8">\n\n  $(document).ready(function(){\n   \n     $(''.submit'').click(function(){\n         \n          var a = document.getElementById(''answer{SGQ}'').value;\n          if (a.length == 0){ \n            \n          }\n          else {\n             alert(''Leave it blank'');\n             return false;\n          }\n         \n     });//click function\n  \n\n\n});//end of document ready\n\n</script>Please begin.<br />\n<br />\n<strong>[DO NOT READ] Leave text box blank. This test will be scored later.</strong>', '', '<div>\n	<b>IF THE PARTICIPANT IS SILENT FOR 15 SECONDS OR STOPS BEFORE THE END OF THE TIMER, ENCOURAGE HIM/HER BY SAYING, </b><b>"Can you think of any more animals?" ONLY </b><b>IF THE PARTICIPANT ASKS FOR CLARIFICATION, EXPLAIN THAT ANIMALS INCLUDE BIRDS, INSECTS, FISH, ETC. DO NOT HELP THE PARTICIPANT.</b></div>\n<div>\n	<br />\n	<b>START TIMER FOR 60 SECONDS. TIMER CANNOT BE RESET OR PAUSED. </b></div>\n<div>\n	 </div>\n<div>\n	 </div>\n<br />\n<br />\n', 'N', 'N', 33, 'en', 0, 0, NULL),
(1894, 0, 78958, 55, 'N', 'COG_ANMLLIST_TRM', '<script type="text/javascript" charset="utf-8">\n\n  $(document).ready(function(){\n   \n     $(''.submit'').click(function(){\n         \n          var a = document.getElementById(''answer{SGQ}'').value;\n          if (a.length == 0){ \n            \n          }\n          else {\n             alert(''Leave it blank'');\n             return false;\n          }\n         \n     });//click function\n  \n\n\n});//end of document ready\n\n</script>Veuillez commencer.<br />\n<br />\n<strong>[NE PAS LIRE] Ne rien inscrire dans l''espace. Une note sera accordée plus tard.</strong>', '', '<strong>SI LE PARTICIPANT RESTE SILENCIEUX PENDANT 15 SECONDES OU RESTE BLOQUÉ,  LENCOURAGER EN DISANTPensez-vous à dautres animaux? SEULEMENT SI LE PARTICIPANT DEMANDE DES PRÉCISIONS, EXPLIQUER QUE LES ANIMAUX COMPRENNENT LES OISEAUX, LES INSECTES, LES POISSONS, ETC. NE PAS AIDER LE PARTICIPANT</strong>\n<p>\n	<strong>DÉMARRER LE CHRONOMÈTRE POUR 60 SECONDES. ON NE PEUT REDÉMARRER LE CHRONOMÈTRE OU LE METTRE SUR PAUSE. </strong></p>\n', 'N', 'N', 33, 'fr', 0, 0, NULL),
(1900, 0, 78958, 55, 'X', 'COG_CNTSTRT_TRM', 'Please begin.<br />\n<div>\n	<b>START TIMER (FOR 30 SECONDS) AND THE RECORDING. STOP WHEN THE PARTICIPANT HAS SUCCESSFULLY COUNTED FROM 1 TO 20. ONCE STARTED, THIS QUESTION CANNOT BE RE-ENTERED. THE TIMER CANNOT BE PAUSED OR RESET.DK/RF NOT ALLOWED</b></div>\n<br />\n', '', '<div>\n	 </div>\n<br />\n<br />\n', 'N', 'N', 34, 'en', 0, 0, NULL),
(1900, 0, 78958, 55, 'X', 'COG_CNTSTRT_TRM', '<p>\n	Veuillez commencer.</p>\n<strong>DÉMARRER LE CHRONOMÈTRE (POUR 30 SECONDES) ET COMMENCER L''ENREGISTREMENT. ARRÊTER QUAND LE PARTICIPANT A RÉUSSI  À COMPTER DE 1 À 20. UNE FOIS CETTE QUESTION COMMENCÉE, ON NE PEUT Y REVENIR. ON NE PEUT REDÉMARRER LE CHRONOMÈTRE OU LE METTRE SUR PAUSE. NSP / RF NON PERMIS</strong><br />\n', '', '<div>\n	 </div>\n<br />\n<br />\n', 'N', 'N', 34, 'fr', 0, 0, NULL),
(1901, 0, 78958, 55, 'N', 'COG_CNTTIME_REC_TRM', '<b>RECORD EXACT TIME IN SECONDS, CATI MASK: MIN=1, MAX=30</b>', '', '', 'N', 'Y', 35, 'en', 0, 0, NULL),
