attributes.attribute:
// High Blood Pressure
            var PM_DIASTOLIC_MEASURE = $this('PM_DIASTOLIC_MEASURE').group([85], [999]).map({
            '-85': '0',
            '85+': '1',
            '999': '9'
            },9);
            var PM_SYSTOLIC_MEASURE = $this('PM_SYSTOLIC_MEASURE').group([130], [999]).map({
            '-130': '0',
            '130+': '1',
            '999': '9'
            },9);
            var MEDI_HBP = $this('MEDI_HBP').map({'0':0,'1':1},9,9);
            var hbp;
            if (PM_DIASTOLIC_MEASURE.any('9')
            .and(PM_SYSTOLIC_MEASURE.any('9'))
            .and(MEDI_HBP.any('9')).value()) {
            hbp = newValue('9');
            } else {
            hbp = PM_DIASTOLIC_MEASURE.any('1')
            .or(PM_SYSTOLIC_MEASURE.any('1'))
            .or(MEDI_HBP.any('1'))
            .map({
            'false':0,
            'true':1},9);
            }

            // Diabete
            /*var LAB_GLUC = $this('LAB_GLUC').group([6.1], [99]).map({
            '-6.1': '0',
            '6.1+': '1',
            '99': '9'
            },9);*/
            var MEDI_DIAB = $this('MEDI_DIAB').map({'0':0,'1':1},9,9);
            var diab;
            if (MEDI_DIAB.any('9').value()) {
            diab = newValue('9');
            } else {
            diab = MEDI_DIAB.any('1')
            .map({
            'false':0,
            'true':1},9);
            }

            // Cholesterol
            var GENDER = $this('GENDER');
            var LAB_HDL = $this('LAB_HDL');
            var LAB_HDL_M = LAB_HDL.group([1.03], [99]).map({
            '-1.03': '1',
            '1.03+': '0',
            '99': '9'
            },9);
            var LAB_HDL_F = LAB_HDL.group([1.3], [99]).map({
            '-1.3': '1',
            '1.3+': '0',
            '99': '9'
            },9);
            var chol = GENDER.map({
            '0':LAB_HDL_M,
            '1':LAB_HDL_F,
            '9':9},9);

            // Triglyceride
            var LAB_TRIG = $this('LAB_TRIG').group([1.7], [99]).map({
            '-1.7': '0',
            '1.7+': '1',
            '99': '9'
            },9);
            var MEDI_LPD = $this('MEDI_LPD').map({'0':0,'1':1},9,9);
            var MEDI_NAME_LPD_FIBR_NICO = $this('MEDI_NAME_LPD_FIBR_NICO').map({'0':0,'1':1},9,9);
            var tri;
            if (LAB_TRIG.any('9').and(MEDI_LPD.any('9')).and(MEDI_NAME_LPD_FIBR_NICO.any('9')).value())
            {
            tri = newValue('9');
            } else {
            tri = LAB_TRIG.any('1')
            .or(MEDI_LPD.any('1'))
            .or(MEDI_NAME_LPD_FIBR_NICO.any('1'))
            .map({
            'false':0,
            'true':1},9);
            }

            // Syndrom count
            var val = 0;
            if (hbp.any('9').and(diab.any('9')).and(chol.any('9')).and(tri.any('9')).value())
            {
            // all syndrom info are missing
            val = 9;
            }
            else {
            if (hbp.any('1').value()) { val = 1; }
            if (diab.any('1').value()) { val += 1; }
            if (chol.any('1').value()) { val += 1; }
            if (tri.any('1').value()) { val += 1; }
            }
            val;

